.container {
  width: 100%;
  height: 100%;
}

.chat-wrapper {
  margin-top: 8vh;
  width: $width;
  height: $height;
  max-width: 1280px;
  position: fixed;
  right: 0%;
  z-index: 999;

  .official-link {
    display: flex;
    text-decoration: none;
    color: #38c9ff;
    width: fit-content;
    float: right;
    height: 45px;
    align-items: center;
  }
}

.chatroom{
  height: 100%;
  width: 700px;
  position: fixed;
  right: 0%;
  top: 0%;
  float: right;
  z-index: 999;
  overflow: auto;
}

.chatroom-enter-active {
  transition: all 0.5s ease;
}
.chatroom-leave-active {
  transition: all .5s ease;
}
.chatroom-enter, .chatroom-leave-to{
  transform: translateX(50px);
  opacity: 0;
}

.chatroom-float{
  width: 50px;
  height: 50px;
  border-radius: 50px;
  float: right;
  position: fixed;
  top: 50%;
  right: 2%;
  z-index: 999;
}

.float{
  width: 50px;
  height: 50px;
  border-radius: 50px;
  float: right;
  position: fixed;
  top: 50%;
  right: 10%;
  z-index: 999;
}

.bottom{
  position: fixed;
  bottom: 0%;
  width: 100%;
  height: 5%;
  left: 0%;
  z-index: 999;
}

.player{
  left: 0%;
  height: 0%;
  width: 100%;
  height: 90%;
  z-index: 2;
}

.record{
  position: fixed;
  left: 2%;
  top: 90%;
  float: left;
  z-index: 2;
}

.float-enter-active {
  transition: all .8s ease;
}
.float-enter, .float-leave-to{
  transform: translateX(-60px);
  opacity: 0;
}

.wrap {
  background-color: #ffffff;
  display: flex;
  justify-content: center;
  align-items: center;
}

.fullscreen {
  position: fixed;
  left: 1%;
  top: 50%;
  float: left;
  z-index: 999;
}

.exitRoom{
  position: fixed;
  left: 1%;
  top: 40%;
  float: left;
  z-index: 999;
}

.baberrage {
  width: 100%;
  height: 30%;
  margin: 0;
  position: fixed;
  z-index: 3;
}

/* 设置滚动条的样式 */
::-webkit-scrollbar {
  width: 3px;
  height: 3px;
}

/* 滚动槽 */
::-webkit-scrollbar-track {
  border-radius: 10px;
}

/* 滚动条滑块 */
::-webkit-scrollbar-thumb {
  border-radius: 10px;
  background: rgba(0, 0, 0, 0.1);
}
